#ifdef CLEAR_FLAGS
void msc_clear_flags (void) {
	gpit = max_flags; while (gpit --) flags [gpit] = 0;
}
#endif

unsigned char read_byte (void) {
	return *script ++;
}

unsigned char read_vbyte (void) {
	sc_c = *script ++;
	if (sc_c & 128) return flags [sc_c & 127];
	return sc_c;
}

void readxy (void) {
	sc_x = read_byte ();
	sc_y = read_byte ();
}

void stop_player (void) {
	pvx = pvy = 0;
}

void reloc_player (void) {
	px = read_vbyte () << 10;
	py = read_vbyte () << 10;
	stop_player ();
}

void run_script (unsigned char whichs) {
	// read address offset from index
	gp_gen = (unsigned char *) script_pool + whichs + whichs;
	rda = *gp_gen ++; rdb = *gp_gen;
	script_result = 0;
	if (!(rda | rdb)) return;
	script = (unsigned char *) script_pool + rda + (rdb << 8);

	// todo : update selected item flag

	while ((sc_c = read_byte ()) != 0xff) {
		next_script = script + sc_c;
		sc_terminado = sc_continuar = 0;
		while (!sc_terminado) {
			switch (read_byte ()) {
				case 0x10:
					// IF FLAG sc_x = sc_n
					// Opcode: 10 sc_x sc_n
					readxy ();
					sc_terminado = (flags [sc_x] != sc_y);
					break;
				case 0x12:
					// IF FLAG sc_x > sc_n
					// Opcode: 12 sc_x sc_n
					readxy ();
					sc_terminado = (flags [sc_x] <= sc_y);
					break;
				case 0x20:
					// IF PLAYER_TOUCHES x, y
					// Opcode: 20 sc_x sc_y
					readxy ();
					sc_x <<= 4; sc_y <<= 4;
					sc_terminado = (!(prx + 7 >= sc_x && prx <= sc_x + 15 && pry + 15 >= sc_y && pry <= sc_y + 15));
					break;
				case 0x60:
					 // IF JUST_PUSHED
					 // Opcode: 60
					 sc_terminado = (!just_pushed);
					 break;
				case 0xF0:
					 // IF TRUE
					 // Opcode: F0
					 break;
				case 0xff:
					// then
					// opcode: ff
					sc_terminado = sc_continuar = 1;
					break;
			}
		}

		if (sc_continuar) {
			sc_terminado = 0;
			while (!sc_terminado) {
				switch (read_byte ()) {
					case 0x01:
						// SET FLAG sc_x = sc_n
						// Opcode: 01 sc_x sc_n
						readxy ();
						flags [sc_x] = sc_y;
						break;
					case 0x10:
						// INC FLAG sc_x, sc_n
						// Opcode: 10 sc_x sc_n
						readxy ();
						flags [sc_x] += sc_y;
						break;
					case 0x20:
						// SET TILE (sc_x, sc_y) = sc_n
						// Opcode: 20 sc_x sc_y sc_n
						readxy ();
						_x = sc_x; _y = sc_y; _t = read_vbyte (); map_set ();
						break;
					case 0x31:
						// DEC LIFE sc_n
						// Opcode: 31 sc_n
						plife -= read_vbyte ();
						break;
					case 0x51:
						// SET_FIRE_ZONE x1, y1, x2, y2
						// Opcode: 51 x1 y1 x2 y2
						fzx1 = read_byte ();
						fzy1 = read_byte ();
						fzx2 = read_byte ();
						fzy2 = read_byte ();
						f_zone_ac = 1;
						break;
					case 0x6D:
						// WARP_TO sc_n sc_x sc_y
						// Opcode: 6D sc_n
						n_pant = read_vbyte ();
						on_pant = 99;
						reloc_player ();
						return;
					case 0x6F:
						// REENTER
						// Opcode: 6F
						//do_respawn = 0;
						on_pant = 99; 
						return;
					case 0x70:
						// SET_TIMER a, b
						// Opcode: 0x70 a b
						timer = read_vbyte ();
						timer_frames = 50;
						timer_zero = 0;
						break;
					case 0xE0:
						// SOUND sc_n
						// Opcode: E0 sc_n
						sfx_play (read_vbyte (), 1);
						break;
					case 0xE4:
						// EXTERN sc_n
						// Opcode: 0xE4 sc_n
						do_extern_action (read_byte ());
						break;
					case 0xF1:
						// WIN
						script_result = 1;
						return;
					case 0xff:
						sc_terminado = 1;
						break;
				}
			}
		}
		script = next_script;
	}
}
